INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5404', null, null, 'null', 'null', 'Heritage: Peoples and Institutions', 'Within the context of constructing self, communal and national identities, heritage is of great interest to varying groups of peoples and institutions in societies. Drawing examples from Southeast Asia and beyond, this course looks at the multiple and sometimes conflicting meanings attached to heritage by different stakeholders and consumers. It highlights inequities in the control of heritage objects, places and practices. It pays attention to strategies of fostering discourse between the various stakeholders.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5403', null, null, 'null', 'null', 'Internship', 'The Internship Course for Master’s students is a key experiential learning platform for students who have less than two years of experience in the industry. It provides a formal opportunity for students to join organisations for 12 weeks and 1 week of orientation (over sem. 3-4) to take on an internship position in an area relevant to their field of study, research and/or specialisation. Organisations are vetted and approved by the CNM Department, and their positions must have relevance to the issues undertaken; they involve the application of subject knowledge and theory in reflection upon the work will be assessed.', 8);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACC4761F', null, null, '"ACC3706%:D"', 'null', 'Seminars in Accounting: Advanced Corporate Governance', 'The course seeks to extend students'' understanding of corporate governance derived from ACC3706 by in-depth discussion of selected topics that reflect the instructors'' interests and expertise.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5402', null, null, 'null', 'null', 'Final Year Project', 'This application-led course allows students to work on an individual or group project that blends industry experience with theoretical research. It situates the learning within an application framework by partnering students with a supervisor to develop a project related to arts and cultural entrepreneurship, which may be critical or practice-based. Students will access a platform for supervision with professors, key industry leaders, and peer-to-peer networking. Students will engage critically in theoretical, social, legal, historical, and cultural contexts with reference to the arts and cultural industries, using available resources to tackle industry issues and reflect on the outcomes and process.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACC4761H', null, null, '{"and": ["FIN2704%:D", "ACC2708%:D"]}', 'null', 'Sem. in Acctg: Accounting & Business Analysis for Banks', 'This course seeks to strengthen students’ accounting knowledge and skills necessary to read and analyse the financial information of banks. The banking industry is one of the key pillars of modern economy and plays the unique role as an intermediary providing liquidity for the economy, facilitating commerce and providing credit to business enterprises and individuals.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5401', null, null, 'null', 'null', 'Approaches to Arts & Cultural Entrepreneurship', 'This course will introduce students to ideas, concepts and conditions underpinning Arts & Cultural Entrepreneurship. How does arts & cultural entrepreneurship depart from arts/cultural management? Can arts & culture share complementary aims and beliefs with business and mercantilism? This interdisciplinary course will coalesce material from business studies, cultural studies, the social sciences and the arts to ideate notions of Arts and Cultural Entrepreneurship rooted in a global, historical and local context. The course will also draw knowledge from social enterprises, tourism studies and design studies.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5410', null, null, 'null', 'null', 'Cultural Diplomacy and Intelligence', 'Culture is a key driver for diplomacy and intelligence. This course examines how the soft power of cultural diplomacy has fundamentally shaped global trade elations and cultural complexity. It will also introduce cultural intelligence as a framework to understand the international and organisational complexities of culture. Students completing the course will learn: soft power applications to culture; management approaches to cultural intelligence; cultural complexity theory; everyday multiculturalism and cultural diversity planning, across a range of sites and case studies including education, art, media and tourism, the multi-ethnic workplace, the cross-cultural marketplace, social contact learning, cultural statistics and creative industries.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5409', null, null, 'null', 'null', 'Applied Arts Research and Practice', 'In this course, students will learn about approaches to arts-based research and arts evaluation, from both theoretical and practical perspectives. They will learn about arts-based research as a reflexive, collaborative and creative form of knowledge production. They will learn about the diverse practical applications of arts research and engage in an in-depth manner with a particular arts research case study. They will apply and experiment with interdisciplinary approaches to arts evaluation. By the end of semester, students will develop their own arts research proposal, addressing a particular creative or cultural problem.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5405', null, null, 'null', 'null', 'Cultural Industries and the Law', 'This course provides a broad introduction to international legal frameworks governing and adjudicating arts and cultural practices and productions. This course covers the principles and concepts governing rights and indemnities related to arts and culture. Issues covered in the course may include: copyright and intellectual property laws; international contract law; regulations framing arts and cultural practices; contract-writing and negotiations; legal due-diligence pertaining to arts and cultural resources; international business regulations pertaining to the capitalisation of arts and cultural resources, amongst others.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5406', null, null, 'null', 'null', 'Arts Business', 'Arts Business provides students interested in the arts and/or business with an opportunity to acquire analytical tools as well as practical toolkits. Building upon an art historical survey of intersections between art and the socio-economical milieu, the course introduces a broad spectrum of funding models from artist to art institutions while carefully examine notable artistic productions challenging these existing mechanisms. Students will not only expand their understanding of the creative output from a socioeconomic perspective but also will have a chance to apply their theoretical lessons by turning their studies into a practical and functioning budget scheme.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5408', null, null, 'null', 'null', 'Managing Cultural Events', 'Managing Cultural Events provides students interested in the arts and/or business with an opportunity to develop an idea for a cultural event and turn it into a sustainable community. This course examines the types of arts and cultural festivals and events worldwide, exploring the history and significance of such these events. It provides practical information and case studies on producing a live event or festival, including programming, budgeting, obtaining funding, risk assessment and evaluation. Along with encouraging innovation and creative/critical thinking about the role of arts and culture in society, the course examines theories and practical techniques for project management.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5407', null, null, 'null', 'null', 'Museums, Exhibitions and the Curatorial', 'This course is designed to help students understand a range of practical and theoretical concerns in a museum, the role and expression of curatorial practice, and the procedure in exhibition making. It covers fundamental concepts in museology, curatorial studies, and exhibition development. This course also investigates how a museum’s history defines its policies and narrative practices by introducing students to subjects including collecting strategies, modes of displaying and exhibition organisation. A set of field-based experiences will give students opportunities to become familiar with a specific approach to curatorial practice, such as permanent exhibitions, thematic exhibitions, and alternative curatorial practice.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5413', null, null, 'null', 'null', 'Consulting as an Advanced Communication & Career Skill', 'Drawing on organisational communication research and management scholarship, this course aims to help postgraduate students develop advanced consulting skills to demonstrate their value creation at work. As consulting involves communicative processes, students will learn about the role of self-, others-, and situational-awareness in identifying what is considered of value, to whom, and in which contexts when working for clients. Building on awareness, students will learn how to develop their domain, process, and context expertise, and better manage clients and workplace stakeholders, thus unlocking value for their firm and deepening their client relationships.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5411', null, null, 'null', 'null', 'Cultural Analytics and Informatics', 'This course introduces key computational theories and applications in analysing and mediating culture. Students will gain both theoretical and practical knowledge of how cultural objects can be quantitatively analysed and how their meanings are dynamically shaped by applications of new media and technologies. The theme of analytics and informatics are explored throughout the course, and interrogated through four major blocks representing the key stages of culture: cultural production, experience and encounters, dissemination and interpretation in Asia. Students will be encouraged to think critically about the intersections of technology, stakeholders and culture. Prior computational knowledge is not required for this course.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5412', null, null, 'null', 'null', 'Mentorship', 'Mentorship is a key experiential learning platform for students who have more than two years of industry experience. This provides a formal opportunity for students to join organisations for 12 weeks plus 1 week of orientation (over special terms) to take on a mentored position with a senior leader on aspects of cultural entrepreneurship in the arts, culture and heritage (ACH). These leaders and organisations are vetted and approved by the Department, and the students’ positions must have relevance to the issues undertaken that involve the application of subject knowledge and theories in cultural entrepreneurship. The Committee would like to seek clarification on the differences between the Mentorship course and the Internship course.URL: about:blank', 8);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('ACE5480', null, null, 'null', 'null', 'Topics in Arts and Cultural Entrepreneurship', 'Topics in Arts and Cultural Entrepreneurship introduces special, advanced or rotating topics currently not included in the regular curriculum, or builds on the basic courses in the curriculum. Topics in this course offer instruction on various specialisations related to creative entrepreneurship, more advanced instruction on the topics/issues covered in the current courses , and/or research, discussion and analysis of issues of current interest related to the cultural and creative industries. The content of the course will therefore vary according to the specialised interests of the lecturer teaching the course.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('AH2201', null, null, '"AH2101:D"', 'null', 'Chinese Painting: Styles and Masters', 'This is an introductory-level course, providing students a historical survey of three thousand years of Chinese visual arts with emphasis on painting. Through the course, students will gain a basic understanding of the historical transformation of Chinese art from the classical towards the modern and contemporary, as well as key aesthetic and philosophical conceptions underpinning the production of visual arts in the Chinese culture. In addition, the course provides some comparative studies of Chinese and Western visual arts. There will also be a component introducing the special linkages between the history of Singapore art and the Chinese context.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('AH2202', null, null, '"AH2101:D"', '["EU4401", "EU4660", "EU4214", "HY4230", "HY4230HM", "EU4660HM", "EU4214HM"]', 'Modern Art: A Critical Introduction', 'What is modern art? How has it been understood and interpreted by artists, critics and art historians? What is the relationship between modern art, modernism and modernity? Is the history of modern art “multiple”? The course will explore these questions through a chronological introduction to modern art, from the 19th century to the 1950s. Students will be encouraged to critically-analyse visual and textual primary-source material to develop a nuanced understanding of different developments in modern art. Case studies on modern art in Asia will also be included to encourage students to appreciate the multiplicity and global diffusion of modern art.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('AH3201', null, null, '"AH2101:D"', 'null', 'A History of Contemporary Art', 'This course provides an introduction to key movements and tendencies in late modern and contemporary art, primarily in Europe and North America, but also extending to related developments around the world. Through a close analysis of significant artists, exhibitions and texts, the course will encourage a historical understanding of the emergence of Western contemporary art and its role within the globalised art world of today. The influences of social, political and cultural forces will also be discussed, providing a wider framework for students to interpret artworks and to examine their context and reception.', 4);
INSERT INTO course_details 
(module_code, exam_date, exam_duration, prereq_tree, fulfill_requirements, title, description, module_credit)
VALUES 
('AH2203', null, null, '"AH2101:D"', 'null', 'Empire and Art: India, Singapore, Malaya', 'This course surveys art and architectural genres produced in British colonies, chiefly India, Singapore and Malaya through diverse visual forms such as painting, calendar art, photography, craft objects and buildings. Visual analysis is accompanied by an investigation into the shifts in materials, technologies and contexts of display and consumption, which often expressed British control, native resistance and a desire for self-rule. The course also considers the role of British institutions, namely, art schools, archaeological surveys, museums and exhibitions in grooming art production and tastes; native projects and responses are unravelled simultaneously to understand East-West interactions.', 4);
INSERT INTO course_details 
